${NC}"

# Function to display elapsed time
show_elapsed_time() {
    local current_time=$(date +%s)
    local elapsed=$((current_time - BUILD_START_TIME))
    local minutes=$((elapsed / 60))
    local seconds=$((elapsed % 60))
    echo -e "${BLUE}â±ï¸  Elapsed time: ${minutes}m ${seconds}s${NC}"
}

# Function to check if timeout is approaching
check_timeout_warning() {
    local current_time=$(date +%s)
    local elapsed=$((current_time - BUILD_START_TIME))
    local elapsed_minutes=$((elapsed / 60))
    local warning_threshold=$((TIMEOUT_MINUTES - 5))
    
    if [ $elapsed_minutes -ge $warning_threshold ]; then
        echo -e "${YELLOW}âš ï¸  WARNING: Approaching timeout threshold (${elapsed_minutes}/${TIMEOUT_MINUTES} minutes)${NC}"
        return 0
    fi
    return 1
}

# Pre-deployment checks
echo -e "${YELLOW}ğŸ“‹ Pre-deployment checks...${NC}"

# Activate virtual environment if it exists
if [ -d ".venv" ]; then
    echo -e "${GREEN}âœ… Activating virtual environment...${NC}"
    source .venv/bin/activate
fi

# Check if EB CLI is installed
if ! command -v eb &> /dev/null; then
    echo -e "${RED}âŒ EB CLI not found. Please install it first.${NC}"
    exit 1
fi

# Check if we're in the right directory
if [ ! -f "Dockerfile" ]; then
    echo -e "${RED}âŒ Dockerfile not found. Please run from the backend directory.${NC}"
    exit 1
fi

# Check if .ebextensions timeout config exists
if [ ! -f ".ebextensions/00_timeout.config" ]; then
    echo -e "${RED}âŒ Timeout configuration not found. Please ensure 00_timeout.config exists.${NC}"
    exit 1
fi

echo -e "${GREEN}âœ… Pre-deployment checks passed${NC}"

# Display timeout configuration
echo -e "${BLUE}â° Extended timeout configuration active:${NC}"
echo -e "   â€¢ Command timeout: ${TIMEOUT_MINUTES} minutes"
echo -e "   â€¢ Docker daemon timeout: ${TIMEOUT_MINUTES} minutes"
echo -e "   â€¢ Rolling deployment enabled"
echo -e "   â€¢ Enhanced health reporting enabled"

# Test Docker build locally first (optional)
read -p "ğŸ” Test Docker build locally first? (y/n): " -n 1 -r
echo
if [[ $REPLY =~ ^[Yy]$ ]]; then
    echo -e "${YELLOW}ğŸ”§ Testing Docker build locally...${NC}"
    
    # Build with multi-stage optimizations
    if docker build -t bunklogs-backend-test . --progress=plain; then
        echo -e "${GREEN}âœ… Local Docker build successful${NC}"
        docker rmi bunklogs-backend-test 2>/dev/null || true
    else
        echo -e "${RED}âŒ Local Docker build failed. Please fix issues before deploying.${NC}"
        exit 1
    fi
fi

# Start deployment
echo -e "${BLUE}ğŸš€ Starting Elastic Beanstalk deployment...${NC}"
show_elapsed_time

# Initialize EB if needed
if [ ! -f ".elasticbeanstalk/config.yml" ]; then
    echo -e "${YELLOW}ğŸ“ Initializing EB configuration...${NC}"
    eb init --platform docker --region us-east-1
fi

# Create deployment with timeout monitoring
echo -e "${BLUE}ğŸ“¦ Deploying to Elastic Beanstalk...${NC}"

# Start deployment in background to monitor progress
eb deploy --timeout $((TIMEOUT_MINUTES * 60)) &
DEPLOY_PID=$!

# Monitor deployment progress
while kill -0 $DEPLOY_PID 2>/dev/null; do
    sleep 30
    show_elapsed_time
    
    if check_timeout_warning; then
        echo -e "${YELLOW}ğŸ’¡ Tip: Multi-stage Docker build should reduce build time significantly${NC}"
    fi
done

# Wait for deployment to complete
wait $DEPLOY_PID
DEPLOY_EXIT_CODE=$?

